import classNames from 'classnames';
import debounce from 'lodash.debounce';
import React from 'react';
import type { FC } from 'react';

import type { PopoverTriggerProps } from './types';

const selectorPrefix = 'adhere-mobile-ui-ant-hoc-popover-trigger';

const DEFAULT_TRIGGER_ICON =
  'data:image/svg+xml;base64,';

/**
 * PopoverTrigger
 * @param className
 * @param style
 * @param changeValue
 * @param renderTrigger
 * @param renderPopover
 * @constructor
 */
const PopoverTrigger: FC<PopoverTriggerProps> = ({
  className,
  style,
  value,
  renderTrigger,
  renderPopover,
  disabled = false,
  beforeTrigger,
}) => {
  function onTrigger() {
    if (disabled) return;

    function execute() {
      renderPopover?.();
    }

    if (!beforeTrigger) {
      execute();
    } else {
      beforeTrigger().then(() => {
        execute();
      });
    }
  }

  return (
    <div
      className={classNames(selectorPrefix, className ?? '')}
      style={style ?? {}}
      onClick={debounce(onTrigger, 200)}
    >
      {renderTrigger?.(value) ?? <img src={DEFAULT_TRIGGER_ICON} alt="" />}
    </div>
  );
};

export default PopoverTrigger;
